rows.each do |row|
  isbn = row['ISBN']
  next unless updates.key?(isbn)

  row['책 소개'] = updates[isbn][:intro]
  row['목차'] = updates[isbn][:contents]
  row['출판사 서평'] = updates[isbn][:review]
  row['본문 인용'] = updates[isbn][:quote]
end

CSV.open(input_file, 'w', col_sep: "\t", write_headers: true, headers: rows.headers) do |csv|
  rows.each do |row|
    csv << row
  end
end

puts "Updated data for 6 books."
